import React from "react";
import './ExpenseItem.css'
const ExpenseItem = (props) => {
    // {props.eDate.toLocaleDateString()}
    const day = props.eDate.toLocaleDateString('en-US',{day: '2-digit'})
    const month = props.eDate.toLocaleDateString('en-US',{month:'long'})
    const year = props.eDate.getFullYear()
    return (
        <div className="expense-item">
            <div>
                <div>{day}</div>
                <div>{month}</div>
                <div>{year}</div>
            </div>
            <div className="expense-item__description">
                <h2 >{props.eItem}</h2>
                <div className="expense-item__price"> ${props.eAmount} </div>
            </div>

        </div>
    )
}

export default ExpenseItem;
