import ExpenseItem from "../ExpenseItem/ExpenseItem";
import Card from "../../UI/Card/Card";
import React, { useState } from "react";
import "./Expenses.css";
import ExpensesFilter from "../ExpenseFilter/ExpensesFilter";
export const Expenses = (props) => {
  const [InputYear, setInputYear] = useState("2020");

  const yearFilterHandler = (selectedYear) => {
    setInputYear(selectedYear);
  };

  const filteredExpenses = props.expenses.filter((expense) => {
    return InputYear === expense.date.getFullYear().toString();
  });

  let expenseContent = <p className="para">No expenses found</p>
  if(filteredExpenses.length>0){
    expenseContent = filteredExpenses.map((val) => {
        //   if (InputYear === val.date.getFullYear().toString()) {
        return <ExpenseItem val={val} key={val.id} InputYear={InputYear} />;
        //   }
      })
  }
  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter
          onYearSelect={yearFilterHandler}
          selectedYear={InputYear}
        />
        {expenseContent}
      </Card>
    </div>
  );
};
