import ExpenseItem from "../ExpenseItem/ExpenseItem";
import Card from "../../UI/Card/Card";
import React, { useState } from "react";
import "./Expenses.css";
import ExpensesFilter from "../ExpenseFilter/ExpensesFilter";
export const Expenses = (props) => {
  const [InputYear, setInputYear] = useState("2020");

  const yearFilterHandler = (selectedYear) => {
    setInputYear(selectedYear);
  };
  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter
          onYearSelect={yearFilterHandler}
          selectedYear={InputYear}
        />
        {props.expenses.map((val) => {
            if (InputYear===val.date.getFullYear().toLocaleString()){
                return <ExpenseItem val={val} key={val.id} />
            }else return null
        })}
      </Card>
    </div>
  );
};
